mapper.select:
SELECT 
FROM homework_submission
WHERE student_id = #{studentId,jdbcType=BIGINT}
AND homework_id = #{homeworkId,jdbcType=BIGINT}
ORDER BY submit_time DESC

SELECT 
FROM homework_submission
WHERE student_id = #{studentId,jdbcType=BIGINT}
AND homework_id = #{homeworkId,jdbcType=BIGINT}
AND problem_id = #{problemId,jdbcType=BIGINT}
ORDER BY score DESC, submit_time ASC
LIMIT 1

SELECT 
FROM homework_submission
WHERE homework_id = #{homeworkId,jdbcType=BIGINT}
AND student_id = #{studentId,jdbcType=BIGINT}
ORDER BY score DESC
LIMIT 1

SELECT COUNT(DISTINCT student_id)
FROM homework_submission
WHERE homework_id = #{homeworkId,jdbcType=BIGINT}

mapper.update:
UPDATE homework_submission
SET score = #{score,jdbcType=INTEGER},
    status = #{status,jdbcType=VARCHAR}
WHERE id = #{id,jdbcType=BIGINT}
