mapper.select:
SELECT 
FROM learning_statistics
WHERE user_id = #{userId,jdbcType=BIGINT}
AND stat_date = #{statDate,jdbcType=DATE}

SELECT 
FROM learning_statistics
WHERE user_id = #{userId,jdbcType=BIGINT}
AND stat_date BETWEEN #{startDate,jdbcType=DATE} AND #{endDate,jdbcType=DATE}
ORDER BY stat_date ASC

mapper.update:
UPDATE learning_statistics
submit_count = #{submitCount,jdbcType=INTEGER}, accept_count = #{acceptCount,jdbcType=INTEGER}, problem_solved = #{problemSolved,jdbcType=INTEGER}, code_lines = #{codeLines,jdbcType=INTEGER}, study_duration = #{studyDuration,jdbcType=INTEGER},
WHERE id = #{id,jdbcType=BIGINT}

UPDATE learning_statistics
SET submit_count = submit_count + #{submitCount,jdbcType=INTEGER},
    accept_count = accept_count + #{acceptCount,jdbcType=INTEGER},
    problem_solved = problem_solved + #{problemSolved,jdbcType=INTEGER},
    code_lines = code_lines + #{codeLines,jdbcType=INTEGER}
WHERE user_id = #{userId,jdbcType=BIGINT}
AND stat_date = #{statDate,jdbcType=DATE}
