mapper.select:
SELECT 
FROM wrong_problem
WHERE user_id = #{userId,jdbcType=BIGINT}
AND problem_id = #{problemId,jdbcType=BIGINT}
ORDER BY created_time DESC
LIMIT 1

SELECT 
FROM wrong_problem
WHERE user_id = #{userId,jdbcType=BIGINT}
AND error_type = #{errorType,jdbcType=VARCHAR}
ORDER BY created_time DESC

mapper.update:
UPDATE wrong_problem
error_type = #{errorType,jdbcType=VARCHAR}, wrong_count = #{wrongCount,jdbcType=INTEGER}, is_resolved = #{isResolved,jdbcType=INTEGER}, resolved_time = #{resolvedTime,jdbcType=TIMESTAMP}, updated_time = NOW(),
WHERE id = #{id,jdbcType=BIGINT}

mapper.select:
SELECT COUNT(*)
FROM wrong_problem
WHERE user_id = #{userId,jdbcType=BIGINT}

AND is_resolved = #{isResolved,jdbcType=INTEGER}
