mapper.update:
UPDATE user
username = #{username,jdbcType=VARCHAR}, password = #{password,jdbcType=VARCHAR}, email = #{email,jdbcType=VARCHAR}, phone = #{phone,jdbcType=VARCHAR}, real_name = #{realName,jdbcType=VARCHAR}, avatar = #{avatar,jdbcType=VARCHAR}, role = #{role,jdbcType=VARCHAR}, status = #{status,jdbcType=INTEGER}, school = #{school,jdbcType=VARCHAR}, student_id = #{studentId,jdbcType=VARCHAR}, grade = #{grade,jdbcType=VARCHAR}, major = #{major,jdbcType=VARCHAR}, updated_time = NOW(),
WHERE id = #{id,jdbcType=BIGINT}

mapper.select:
SELECT 

FROM user
WHERE deleted = 0

    AND (username LIKE CONCAT('%', #{keyword}, '%') 
    OR email LIKE CONCAT('%', #{keyword}, '%'))


    AND role = #{role,jdbcType=VARCHAR}


    AND status = #{status,jdbcType=INTEGER}

ORDER BY created_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM user WHERE deleted = 0
AND (username LIKE CONCAT('%', #{keyword}, '%') OR email LIKE CONCAT('%', #{keyword}, '%'))
AND role = #{role,jdbcType=VARCHAR}
AND status = #{status,jdbcType=INTEGER}
